var exec = require('child_process').exec;
function iniciar()
{
    console.log("Se ha llamado a la funcion de iniciar");
    var content = "empty";
    exec("ls -lah",function(error,stdout,stdin){content = stdout;});
    return content;
}
function subir()
{
    console.log("Se ha llamado a la funcion de subir");
    return "llamada la funcion subir";
}

exports.iniciar = iniciar;
exports.subir = subir;