var command = require('child_process').spawn;

exports.get_info = function(filepath,return_info)
{
        command('mplayer',["-vo", "null", "-ao", "null", "-frames", "0" ,"-identify",filepath]);
        command.stdout.on('data',function(data){});
        command.on('exit',function(code){});
}

exports.get_thumbnail = function(video,time,folder)
{
        var thumbnail_name = "";
        command('mplayer',["-ss", time, video,"-frames", "1" ,"-nosoud","-vo","png:outdir="+folder+"/"+thumbnail_name]);
        command.stdout.on('data',function(data){});
        command.on('exit',function(code){});
}